import React, {Component} from 'react';
import handleChange from "./utils"

function Selection(props) {
    const { selectedWorkouts } = props;

    workoutDeselect = (exercise) => {
        this.setState(state => {
          const selected = [...state.selectedWorkouts];
          selected.splice(selected.indexOf(exercise.title))
          return ({
            selected
          })
        })
      }
    
    return (
        <div className="col-md-3">
            <h1>Selection <i class="fa fa-plus"></i></h1>
            <ul id="selectionList">
            { selectedWorkouts.map(exercise => {
                        return <li key={exercise._id}><span onClick={() => this.workoutSelect(exercise)} value={exercise.title} className="plus"><i class="fa fa-plus"></i></span>{exercise.title}<span onClick={() => this.workoutDeselect(exercise)}><i class="fa fa-trash"></i></span></li>
                      })}                  
            </ul>
        </div>
    )
}
export default Selection;

