import React, { Component, setState, useState } from "react";
import { Link } from 'react-router-dom';
import Flatbench from "./exercises/Flatbench";
import Selection from "./Selection";
import axios from "axios";
import Exercises from "./Exercises";


export default class HomePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
        workouts: [],
        selectedWorkouts: [],
        completedWorkouts: []
    };
  }

  componentDidMount = () => {
    axios.get("/getworkouts").then(workouts => {
        this.setState({workouts: workouts.data.workouts});
    })
  };

  workoutSelect = (exercisePacket) => {
    const exercise = exercisePacket.target.value;
    this.setState({selectedWorkouts: exercise})
    console.log(exercisePacket)
  }

  render() {
    const { workouts, selectedWorkouts, completedWorkouts } = this.state;

    

    return(
      <div>
        <div className="jumbotron">
          <h2>Workout React App</h2>
          <p>Sweat</p>
          <Link to="workout1" className='btn btn-primary'>Workout 1</Link>
          <Link to="workout2" className='btn btn-primary'>Workout 2</Link>
        </div>
        <div className="container-fluid">
          <div className="row">
              <div className="col-md-4">
                  <h1>Exercises <i class="fa fa-plus"></i></h1>
                  <ul id="selectionList">
                      { workouts.map( exercise => {
                              return <li key={exercise._id}><span onClick={this.workoutSelect} className="plus"><i class="fa fa-plus"></i></span>{exercise.title}</li>
                          })}      
                  </ul>
              </div>
              <Selection selectedWorkouts={selectedWorkouts} />
          </div>
        </div>
      </div>
            
    )
  }
}
