import React, { Component } from "react";
import axios from "axios";
import uuid from "react-uuid";
import RepCounter from "./common/RepCounter";


export default class HomePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
        workouts: [
          {
            _id: "0",
            title: "Flat Bench Press",
            reps: "10x"
          },
          {
            _id: "1",
            title: "Tricep Bench Press",
            reps: "10x"
          },
          {
            _id: "2",
            title: "Single Arm Row",
            reps: "10x"
          },
          {
            _id: "3",
            title: "Dumbell Squat",
            reps: "10x"
          },
          {
            _id: "4",
            title: "Dumbell Squat and Press",
            reps: "10x"
          },
          {
            _id: "5",
            title: "Lunges",
            reps: "10x"
          },
          {
            _id: "6",
            title: "Step Ups",
            reps: "10x"
          },
          {
            _id: "7",
            title: "Dumbell Curls",
            reps: "10x"
          },
          {
            _id: "8",
            title: "Tricep Extensions",
            reps: "10x"
          },
          {
            _id: "9",
            title: "Shoulder Press",
            reps: "10x"
          }
        ],
        selectedWorkouts: [],
        completedWorkouts: []
    };
  }

  // componentDidMount = () => {
  //   axios.get("/getworkouts").then(workouts => {
  //       this.setState({workouts: workouts.data.workouts});
  //   })
  // };

  workoutSelect = (exercise) => {
    this.setState(prevState => ({
       selectedWorkouts: [...prevState.selectedWorkouts, exercise],
      
    }))
  }

  workoutCompleted = (exercise) => {
    const selectedWorkouts = [...state.selectedWorkouts];
    const sw = selectedWorkouts.splice(selectedWorkouts.indexOf(exercise.title))
      
      
    this.setState((prevState, sw) => ({
      completedWorkouts: [...prevState.completedWorkouts, exercise],
      selectedWorkouts: sw
    }))
  } 

  workoutDeselect = (exercise) => {
    this.setState(state => {
      const selectedWorkouts = [...state.selectedWorkouts];
      selectedWorkouts.splice(selectedWorkouts.indexOf(exercise.title))
      return ({
        selectedWorkouts
      })
      
    })
  }

  completedDeselect = (exercise) => {
    this.setState(state => {
      const completedWorkouts = [...state.completedWorkouts];
      completedWorkouts.splice(completedWorkouts.indexOf(exercise.title))
      return ({
        completedWorkouts
      })
      
    })
  }

  
  render() {
    const { workouts, selectedWorkouts, completedWorkouts } = this.state;
    

    

    return(
      <div>
        
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-3">
                <h1>Exercises <i className="fa fa-plus"></i></h1>
                <ul id="selectionList">
                    { workouts.map( exercise => {
                            return <li key={exercise._id}><span onClick={() => this.workoutSelect(exercise)}  className="plus"><i className="fa fa-plus"></i></span>{exercise.title}</li>
                        })}      
                </ul>
            </div>
            <div className="col-md-3">
                <h1>Reps | Selection <i className="fa fa-plus"></i></h1>
                <ul id="selectionList">
                    { selectedWorkouts.map(exercise => {
                        return <li key={uuid()}><span className="repsLi">{exercise.reps}</span><span onClick={() => this.workoutCompleted(exercise)} className="compSp"><i class="fa fa-check"></i></span>{exercise.title}<span onClick={() => this.workoutDeselect(exercise)} className="float-right"><i className="fa fa-trash"></i></span></li>
                    })}                   
                </ul>                
            </div>
            <div className="col-md-3">
                <h1>Completed <i className="fa fa-plus"></i></h1>
                <ul id="selectionList">
                    { completedWorkouts.map(exercise => {
                        return <li key={uuid()}><span className="compLi">{exercise.reps}</span>{exercise.title}<span onClick={() => this.completedDeselect(exercise)} className="float-right"><i className="fa fa-trash"></i></span></li>
                    })}                   
                </ul>                
            </div>
          </div>
        </div>
      </div>
            
    )
  }
}
