import React, { Component } from "react";
import { Link } from 'react-router-dom';
import Flatbench from "./exercises/Flatbench";
import Selection from "./Selection";
import axios from "axios";
import Exercises from "./Exercises";



export default class HomePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
        workouts: [],
        status: "not selected"
    };
  }

  componentDidMount = () => {
    axios.get("/getworkouts").then(workouts => {
        this.setState({workouts: workouts.data.workouts});
        console.log(workouts);
    })
  };

  exercises(props) {
    const { workouts } = props    

    return (
        <div className="col-md-4">
            <h1>Exercises <i class="fa fa-plus"></i></h1>
            <ul id="selectionList">
                { workouts.map( workouts => {
                        return <li key={workouts._id}><span onClick={handleClick} className="plus"><i class="fa fa-plus"></i></span>{workouts.title}</li>
                    })}      
            </ul>
        </div>
    )
}

  render() {
    const { workouts, status } = this.state;
    console.log(this.state);
    return(
      <div>
        <div className="jumbotron">
          <h2>Workout React App</h2>
          <p>Sweat</p>
          <Link to="workout1" className='btn btn-primary'>Workout 1</Link>
          <Link to="workout2" className='btn btn-primary'>Workout 2</Link>
        </div>
        <div className="container-fluid">
          <div className="row">              
              <Exercises workouts={workouts} status={status}/>
              <Selection workouts={workouts} status={status}/>
          </div>
        </div>
      </div>
            
    )
  }
}
