import React, { Component } from "react";
import axios from "axios";
import uuid from "react-uuid";
import RepCounter from "./common/RepCounter";


export default class HomePage extends Component {
  constructor(props) {
    super(props);
    this.state = {
        workouts: [],
        selectedWorkouts: [],
        completedWorkouts: []
    };
  }

  componentDidMount = () => {
    axios.get("/getworkouts").then(workouts => {
        this.setState({workouts: workouts.data.workouts});
    })
  };

  workoutSelect = (exercise) => {
    this.setState(prevState => ({
      workouts: prevState.workouts.map(exercise => exercise.status === "not selected"? {...exercise, status: "selected"}: exercise),
      selectedWorkouts: [...prevState.selectedWorkouts, exercise],
      
    }))
  }

  workoutDeselect = (exercise) => {
    this.setState(state => {
      const selectedWorkouts = [...state.selectedWorkouts];
      selectedWorkouts.splice(selectedWorkouts.indexOf(exercise.title))
      return ({
        selectedWorkouts
      })
      
    })
  }

  
  render() {
    const { workouts, selectedWorkouts, completedWorkouts } = this.state;
    

    

    return(
      <div>
        
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-3">
                <h1>Exercises <i className="fa fa-plus"></i></h1>
                <ul id="selectionList">
                    { workouts.map( exercise => {
                            return <li key={exercise._id}><span onClick={() => this.workoutSelect(exercise)}  className="plus"><i className="fa fa-plus"></i></span>{exercise.title}</li>
                        })}      
                </ul>
            </div>
              <div className="col-md-3">
                  <h1>Reps | Selection <i className="fa fa-plus"></i></h1>
                  <ul id="selectionList">
                      { selectedWorkouts.map(exercise => {
                        console.log(exercise)
                          return <li key={uuid()}><span className="repsLi"><RepCounter /></span>{exercise.title}<span onClick={() => this.workoutDeselect(exercise)} className="float-right"><i className="fa fa-trash"></i></span></li>
                      })}                   
                  </ul>
              </div>
          </div>
        </div>
      </div>
            
    )
  }
}
