import React,{ Component } from "react";
import Header from "./common/Header";
import HomePage from "./HomePage";
import WorkoutPage from "./WorkoutPage";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import { toast, ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export default class Container extends Component {
    constructor(props) {
        super(props);
        this.state = {          
          selectedWorkouts: [],
        };
    }
    

    workoutDeselect = (exercise) => {
        this.setState((state) => {
            const selectedWorkouts = [...state.selectedWorkouts];
            selectedWorkouts.splice(selectedWorkouts.indexOf(exercise.title));
            return {
            selectedWorkouts,
            };
        });
    };

    workoutCompleted = (exercise) => {
        this.setState((state) => {
            console.log(exercise);
            const selectedWorkouts = [...state.selectedWorkouts];
            toast.success("Exercise Completed!");
            selectedWorkouts.splice(selectedWorkouts.indexOf(exercise.title));
            return {
            selectedWorkouts,
            };
        });
    };


    render() {
        const { workoutSelect} = this.state;
        return(
            <Router> 
                <ToastContainer />
                <Header />
                <Switch>
                    <Route path="/" exact render={(props) => (
                        <HomePage {...props}
                          workouts={this.state.workouts} 
                          selectedWorkouts={this.state.selectedWorkouts} 
                          workoutSelect={this.state.workoutSelect}
                        />
                    )}
                    />
                    
                    <Route path="/workout" render={(props) => (
                        <WorkoutPage {...props} 
                          selectedWorkouts={this.state.selectedWorkouts} 
                        />
                    )} />
                </Switch>          
            </Router>
        )
    }
}
